CREATE TABLE find (id BIGINT AUTO_INCREMENT, device_id BIGINT NOT NULL, project_id BIGINT NOT NULL, description TEXT NOT NULL, created_date DATETIME NOT NULL, updated_date DATETIME NOT NULL, longtitude DOUBLE, latitude DOUBLE, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = MyISAM;
CREATE TABLE device (id BIGINT AUTO_INCREMENT, imei VARCHAR(128) NOT NULL UNIQUE, name VARCHAR(128), auth_key VARCHAR(128) NOT NULL UNIQUE, registered_date DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = MyISAM;
CREATE TABLE project (id BIGINT AUTO_INCREMENT, title VARCHAR(128) NOT NULL, description TEXT, created_date DATETIME NOT NULL, updated_date DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = MyISAM;
